import cv2
import xlwt
import os
def get_desktop():
    return os.path.join(os.path.expanduser('~'),"Desktop")
deskPath='C:/Users/Administrator/Desktop/' #桌面路径
#读取照片
image = cv2.imread(r'F:\PythonTest\venv\drawer\myimage.jpeg')
image_cv=cv2.cvtColor(image,cv2.COLOR_BGRA2GRAY)
cv2.imshow("myimage",image_cv)
key = cv2.waitKey(0)

#将数据写入Excel 表格
book = xlwt.Workbook()
sheet = book.add_sheet('sheet1', cell_overwrite_ok=True)
sheet.write(0, 0, image_cv)
sheet.write(0, 1, '序列2') # 表示第0行第1列填入“序列2”
book.save(deskPath+'result.xls')


if key==27: #按esc键时，关闭所有窗口
    print(key)
    cv2.destroyAllWindows()






